&lt;!-- DEBUG-VIEW START 1 APPPATH/Views/pages/productosPorCategoria.php --&gt;
&lt;div class=&quot;container-fluid&quot;&gt;
    &lt;!-- Botón para abrir filtros (visible solo en móviles) --&gt;
    &lt;div class=&quot;d-md-none text-end my-3&quot;&gt;
        &lt;button class=&quot;btn btn-outline-primary&quot; type=&quot;button&quot; data-bs-toggle=&quot;collapse&quot;
            data-bs-target=&quot;#sidebarFiltros&quot;&gt;
            Filtros
        &lt;/button&gt;
    &lt;/div&gt;

    &lt;div class=&quot;row&quot;&gt;
        &lt;!-- Sidebar colapsable --&gt;
        &lt;aside class=&quot;col-md-2 mb-4 collapse d-md-block&quot; id=&quot;sidebarFiltros&quot;&gt;
            &lt;div class=&quot;bg-light p-3 rounded shadow-sm&quot;&gt;
                &lt;h5 class=&quot;mb-3&quot;&gt;Filtros&lt;/h5&gt;

                &lt;!-- Contenedor dinámico de filtros --&gt;
                &lt;div class=&quot;mb-3&quot; id=&quot;contenedorFiltrosCheckboxes&quot;&gt;
                    &lt;!-- Aquí se cargarán los checkboxes dinámicamente con JS --&gt;
                &lt;/div&gt;

                &lt;div class=&quot;mb-3&quot;&gt;
                    &lt;label class=&quot;form-label&quot;&gt;Precio&lt;/label&gt;
                    &lt;div class=&quot;input-group&quot;&gt;
                        &lt;span class=&quot;input-group-text&quot;&gt;$&lt;/span&gt;
                        &lt;input type=&quot;number&quot; class=&quot;form-control&quot; name=&quot;precio_min&quot; placeholder=&quot;Mínimo&quot; min=&quot;0&quot;
                            id=&quot;precioMin&quot;&gt;
                        &lt;span class=&quot;input-group-text&quot;&gt;-&lt;/span&gt;
                        &lt;input type=&quot;number&quot; class=&quot;form-control&quot; name=&quot;precio_max&quot; placeholder=&quot;Máximo&quot; min=&quot;0&quot;
                            id=&quot;precioMax&quot;&gt;
                    &lt;/div&gt;
                &lt;/div&gt;

                &lt;button class=&quot;btn btn-primary mt-2 w-100&quot; id=&quot;btnAplicarFiltros&quot;&gt;Aplicar&lt;/button&gt;
            &lt;/div&gt;
        &lt;/aside&gt;

        &lt;!-- Productos --&gt;
        &lt;div class=&quot;col-md-10&quot;&gt;
            &lt;div class=&quot;row g-3&quot; id=&quot;contenedorProductos&quot;&gt;
                &lt;!-- Aquí se insertarán las cards con JS --&gt;
            &lt;/div&gt;
        &lt;/div&gt;
    &lt;/div&gt;
    &lt;script&gt;
        const filtrosPorCategoria = {
            Mascotas: [&#039;Perros&#039;, &#039;Gatos&#039;, &#039;Peces&#039;],
            Campo: [&#039;Semillas&#039;, &#039;Fertilizantes&#039;, &#039;Herramientas&#039;],
            Insumos: [&#039;Papelería&#039;, &#039;Limpieza&#039;, &#039;Tecnología&#039;]
        };

        const productos = [{&quot;id_producto&quot;:&quot;4&quot;,&quot;nombre&quot;:&quot;Heno para Equinos\/Bovinos &quot;,&quot;descripcion&quot;:&quot;Alimento balanceado para engorde.&quot;,&quot;precio&quot;:&quot;80000.00&quot;,&quot;descuento&quot;:null,&quot;stock&quot;:&quot;50&quot;,&quot;marca&quot;:&quot;Tramontina&quot;,&quot;activo&quot;:&quot;1&quot;,&quot;destacado&quot;:&quot;0&quot;,&quot;fecha_agregado&quot;:&quot;2025-06-06 00:00:00&quot;,&quot;fecha_creacion&quot;:&quot;2025-06-05 22:29:20&quot;,&quot;imagenes&quot;:[{&quot;id_imagen_producto&quot;:&quot;15&quot;,&quot;id_producto&quot;:&quot;4&quot;,&quot;url_imagen&quot;:&quot;1750879725_74ca4f12230feae1f47f.jpeg&quot;,&quot;descripcion_imagen&quot;:&quot;&quot;,&quot;fecha_creacion&quot;:&quot;2025-06-25 19:28:45&quot;}],&quot;imagen&quot;:&quot;http:\/\/localhost\/proyecto_Navarnica\/public\/assets\/img\/img_Productos\/1750879725_74ca4f12230feae1f47f.jpeg&quot;},{&quot;id_producto&quot;:&quot;5&quot;,&quot;nombre&quot;:&quot;Guantes de Trabajo Reforzados&quot;,&quot;descripcion&quot;:&quot;Guantes de cuero para tareas pesadas&quot;,&quot;precio&quot;:&quot;15000.00&quot;,&quot;descuento&quot;:null,&quot;stock&quot;:&quot;10&quot;,&quot;marca&quot;:&quot;3M&quot;,&quot;activo&quot;:&quot;1&quot;,&quot;destacado&quot;:&quot;1&quot;,&quot;fecha_agregado&quot;:&quot;2025-06-06 00:00:00&quot;,&quot;fecha_creacion&quot;:&quot;2025-06-05 22:29:20&quot;,&quot;imagenes&quot;:[{&quot;id_imagen_producto&quot;:&quot;16&quot;,&quot;id_producto&quot;:&quot;5&quot;,&quot;url_imagen&quot;:&quot;1750880078_fb646c674ce6078cb45d.jpg&quot;,&quot;descripcion_imagen&quot;:&quot;&quot;,&quot;fecha_creacion&quot;:&quot;2025-06-25 19:34:38&quot;}],&quot;imagen&quot;:&quot;http:\/\/localhost\/proyecto_Navarnica\/public\/assets\/img\/img_Productos\/1750880078_fb646c674ce6078cb45d.jpg&quot;},{&quot;id_producto&quot;:&quot;6&quot;,&quot;nombre&quot;:&quot;Tanque Pulverizador 20L&quot;,&quot;descripcion&quot;:&quot;Pulverizador manual para agroqu\u00edmicos&quot;,&quot;precio&quot;:&quot;67700.00&quot;,&quot;descuento&quot;:null,&quot;stock&quot;:&quot;10&quot;,&quot;marca&quot;:&quot;FumigAr&quot;,&quot;activo&quot;:&quot;1&quot;,&quot;destacado&quot;:&quot;1&quot;,&quot;fecha_agregado&quot;:&quot;2025-06-06 00:00:00&quot;,&quot;fecha_creacion&quot;:&quot;2025-06-05 22:29:20&quot;,&quot;imagenes&quot;:[{&quot;id_imagen_producto&quot;:&quot;17&quot;,&quot;id_producto&quot;:&quot;6&quot;,&quot;url_imagen&quot;:&quot;1750880132_161b03e5620d4a6759a0.jpg&quot;,&quot;descripcion_imagen&quot;:&quot;&quot;,&quot;fecha_creacion&quot;:&quot;2025-06-25 19:35:32&quot;}],&quot;imagen&quot;:&quot;http:\/\/localhost\/proyecto_Navarnica\/public\/assets\/img\/img_Productos\/1750880132_161b03e5620d4a6759a0.jpg&quot;}];
        const categoriaPrincipal = &quot;Campo&quot;;
        const baseImgPath = &quot;http://localhost/proyecto_Navarnica/&quot;;


        renderizarFiltros(categoriaPrincipal);

        renderizarProductos(productos);


        function renderizarFiltros(categoria) {
            const filtros = filtrosPorCategoria[categoria] || [];
            const contenedor = document.getElementById(&#039;contenedorFiltrosCheckboxes&#039;);
            contenedor.innerHTML = &#039;&#039;;

            filtros.forEach(filtro =&gt; {
                const id = `filtro_${filtro}`;
                contenedor.innerHTML += `
            &lt;div class=&quot;form-check&quot;&gt;
                &lt;input class=&quot;form-check-input&quot; type=&quot;checkbox&quot; name=&quot;filtros[]&quot; value=&quot;${filtro}&quot; id=&quot;${id}&quot;&gt;
                &lt;label class=&quot;form-check-label&quot; for=&quot;${id}&quot;&gt;${filtro}&lt;/label&gt;
            &lt;/div&gt;
        `;
            });
        }

        document.getElementById(&#039;btnAplicarFiltros&#039;).addEventListener(&#039;click&#039;, async () =&gt; {
            const categoria = categoriaPrincipal;
            const filtros = Array.from(document.querySelectorAll(&#039;input[name=&quot;filtros[]&quot;]:checked&#039;)).map(cb =&gt; cb.value);
            const precioMin = document.getElementById(&#039;precioMin&#039;).value || null;
            const precioMax = document.getElementById(&#039;precioMax&#039;).value || null;

            const response = await fetch(&#039;http://localhost/proyecto_Navarnica/api/filtrar-productos&#039;, {
                method: &#039;POST&#039;,
                headers: {
                    &#039;Content-Type&#039;: &#039;application/json&#039;
                },
                body: JSON.stringify({
                    categoria,
                    filtros,
                    precio_min: precioMin,
                    precio_max: precioMax
                })
            });

            if (!response.ok) {
                const texto = await response.text();
                console.error(&quot;Error del servidor:&quot;, texto);
                return;
            }

            const productos = await response.json();

            renderizarProductos(productos);
            console.log(productos);
        });

        function renderizarProductos(productos) {
            const contenedor = document.getElementById(&#039;contenedorProductos&#039;);
            contenedor.innerHTML = &#039;&#039;; // Limpia los productos actuales

            if (productos.length === 0) {
                contenedor.innerHTML = `
                &lt;div class=&quot;text-center my-5&quot;&gt;
                    &lt;i class=&quot;bi bi-box-seam&quot; style=&quot;font-size: 3rem; color: #6c757d;&quot;&gt;&lt;/i&gt;
                    &lt;h4 class=&quot;mt-3&quot;&gt;No se encontraron productos con esos filtros&lt;/h4&gt;
                &lt;/div&gt;
                `;
                return;
            }



            productos.forEach(prod =&gt; {
                contenedor.innerHTML += `

                    &lt;div class=&quot;col-12 col-md-6 col-lg-3 mb-4&quot;&gt;
                        &lt;a href=&quot;http://localhost/proyecto_Navarnica/producto?id=${prod.id_producto}&quot; class=&quot;text-decoration-none&quot;&gt;
                            &lt;div class=&quot;card h-100&quot;&gt;
                                &lt;img src=&quot;${prod.imagen}&quot; class=&quot;card-img-top&quot; alt=&quot;${prod.nombre}&quot;&gt;
                                &lt;div class=&quot;card-body&quot;&gt;
                                &lt;h5 class=&quot;card-title&quot;&gt;${prod.nombre}&lt;/h5&gt;
                                &lt;p class=&quot;card-text&quot;&gt;${prod.descripcion}&lt;/p&gt;
                                &lt;p class=&quot;card-text&quot;&gt;Stock: ${prod.stock}&lt;/p&gt;
                                &lt;p class=&quot;card-text&quot;&gt;$${prod.precio}&lt;/p&gt;
                                &lt;/div&gt;
                            &lt;/div&gt;
                        &lt;/a&gt;
                    &lt;/div&gt;

                `;
            });
        }
    &lt;/script&gt;
&lt;/div&gt;
&lt;!-- DEBUG-VIEW ENDED 1 APPPATH/Views/pages/productosPorCategoria.php --&gt;
